export type TypedFilesModulesConfig = {
  /**
   * @description Import location for types generated by typescript operations
   * plugin.
   */
  typesModule: string;

  /**
   * @default false
   * @description By default, only the filename is used to generate TS module
   * declarations. Setting this to `true` will generate it with a full path
   * based on the CWD.
   */
  relativeToCwd?: boolean;

  /**
   * @default "*\/"
   * @description By default all module paths will be prefixed with "*\/".
   * Override this to remove or specify a custom prefix (helpful to set to "" in
   * conjunction with relativeToCwd.)
   */
  prefix?: string;

  /**
   * @default ""
   * @description Used in conjunction with relativeToCwd if if imports should be
   * relative to some subdirectory within the cwd, e.g. "src".
   */
  stripPrefix?: string;

  /**
   * @default "@graphql-typed-document-node/core"
   * @description Import location for TypedDocumentNode.
   */
  typedDocumentNodeModule?: string;

  /**
   * @default false
   * @description Exclude default exports from modules.
   */
  excludeDefaultExports?: boolean;

  /**
   * @default undefined
   * @description Add suffix to generated operation result type names
   * e.g we have operation name Author and suffix QueryType, we will get AuthorQueryType
   */
  operationResultSuffix?: string | null;

  /**
   * @default false
   * @description use operation name as suffix
   * e.g we have operation query Author, we will get AuthorQuery
   * e.g we have operation mutation Author? we will get AuthorMutation
   */
  useOperationNameAsSuffix?: boolean;
};
